import React, { useState } from 'react';
import { useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Link, useParams } from 'react-router-dom';

const EditUser = () => {
  const { id } = useParams();

  const users = useSelector((state) => state);

  const currentUser = users.find((user) => user.id === parseInt(id));
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [number, setNumber] = useState('');
  useEffect(() => {
    if (currentUser) {
      setName(currentUser.name);
      setEmail(currentUser.email);
      setNumber(currentUser.number);
    }
  }, [currentUser]);

  return (
    <div className="container">
      {currentUser ? (
        <>
          <h1 className="display-3 my-5 text-center">Edit User: {id}</h1>
          <div className="row">
            <div className="col-md-6 shadow-lg mx-auto p-5">
              <form>
                <div className="form-group p-1">
                  <input
                    type="text"
                    placeholder="Name"
                    className="form-control"
                  />
                </div>
                <div className="form-group p-1">
                  <input
                    type="email"
                    placeholder="Email"
                    className="form-control"
                  />
                </div>
                <div className="form-group p-1">
                  <input
                    type="number"
                    placeholder="Phone Number"
                    className="form-control"
                  />
                </div>
                <div className="form-group d-flex justify-content-center py-2">
                  <input
                    type="submit"
                    value="Update User"
                    className=" btn btn-sm btn-dark me-3 "
                  />
                  <Link to="/add" className="btn btn-sm btn-danger ">
                    Cancel
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </>
      ) : (
        <h1 className="display-3 my-5 text-center">
          user Contact with id {id} not exists
        </h1>
      )}
    </div>
  );
};

export default EditUser;
